mapper.select:
SELECT ir.id, ir.fechainiciorenta, ir.fechafinrenta,
i.id as i_id, i.nombre as i_nombre, i.descripcion as i_descripcion, i.fechalanzamiento as i_fechalanzamiento, i.tarifaxdia as i_tarifaxdia, i.formatorenta as i_formatorenta, i.genero as i_genero
FROM VI_ITEMRENTADO as ir LEFT JOIN VI_ITEMS as i on ir.ITEMS_id=i.id LEFT JOIN VI_TIPOITEM AS ti ON i.TIPOITEM_id=ti.id WHERE ir.CLIENTES_documento=#{idc}

SELECT ir.id, ir.fechainiciorenta, ir.fechafinrenta,
i.id as i_id, i.nombre as i_nombre, i.descripcion as i_descripcion, i.fechalanzamiento as i_fechalanzamiento, i.tarifaxdia as i_tarifaxdia, i.formatorenta as i_formatorenta, i.genero as i_genero
FROM VI_ITEMRENTADO as ir LEFT JOIN VI_ITEMS as i on ir.ITEMS_id=i.id LEFT JOIN VI_TIPOITEM AS ti ON i.TIPOITEM_id=ti.id WHERE ir.ITEMS_id=#{idit}
